package com.ConditionalStatement;

public class ElseIfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int marks =20;
		if(marks>=70) {
			System.out.println("A+");
		}
		else if(marks>=55) {
			System.out.println("A");
		}
		else if(marks>=40) {
			System.out.println("B");
		}
		else {
			System.out.println("F");
		}

	}

}
